import * as React from 'react';
import { InputLabel } from '@mui/material';
import MenuItem from '@mui/material/MenuItem';
import FormControl from '@mui/material/FormControl';
import Select from '@mui/material/Select';

export default function SelectAutoWidth() {
  const [CodSituacao, setcodSituacao] = React.useState('');
  const [Status, setStatus] = React.useState('');

  const handleChange = (e) => {
    setcodSituacao(e.target.value);
    setStatus(e.target.value);

  };

  return (
    <>
      <div>
        <FormControl sx={{ m: 1, minWidth: 80 }}>
          <InputLabel id="demo-simple-select-autowidth-label">Cód. Situação</InputLabel>
          <Select
            labelId="demo-simple-select-autowidth-label"
            id="demo-simple-select-autowidth"
            value={CodSituacao}
            onChange={handleChange}
            autoWidth
            label="codSituacao"
          >
            <MenuItem value="">
              <em>None</em>
            </MenuItem>
            <MenuItem value={10}>Ativo</MenuItem>
            <MenuItem value={20}>Inativo</MenuItem>
          </Select>
        </FormControl>
      </div>

      <div>
      <FormControl sx={{ m: 1, minWidth: 80 }}>
        <InputLabel id="demo-simple-select-autowidth-label">Status</InputLabel>
        <Select
          labelId="demo-simple-select-autowidth-label"
          id="demo-simple-select-autowidth"
          value={Status}
          onChange={handleChange}
          autoWidth
          label="Status"
        >
          <MenuItem value="">
              <em>None</em>
            </MenuItem>
            <MenuItem value={10}>Ativo</MenuItem>
            <MenuItem value={20}>Inativo</MenuItem>
        </Select>
      </FormControl>
    </div>
  </>
  );
  
}
